import React from 'react';
import { Checkbox } from 'react-native-paper';
import {View, Text, StyleSheet, Button} from 'react-native';


export default class Suspeito extends React.Component {

  constructor(props) {
    super(props);
    this.state ={
      Motorista : false,
      Empregada : false,
      Mordomo : false,
      Cozinheiro : false,
      Esposa : false,
      Jardineiro : false,
      Vizinho : false
    }
    
  }
  
  redefinir(){
    this.setState({Motorista : false});
    this.setState({Empregada : false}); 
    this.setState({Mordomo : false}); 
    this.setState({Cozinheiro : false}); 
    this.setState({Esposa : false}); 
    this.setState({Jardineiro : false}); 
    this.setState({Vizinho : false}); 
    
  }


  render() {
    const styles = StyleSheet.create({
      box:{
        backgroundColor:'#ad9d8c', 
        flexDirection:'row',
        justifyContent:'space-between'
      },
      texto:{
        alignSelf: "center",
        paddingLeft: 10,
        paddingRight:10
      },
      box1:{
        backgroundColor:'#e4dacf', 
        flexDirection:'row',
        justifyContent:'space-between'
    
      },
      suspeito:{
        borderRadius: 20,
        borderColor: 'black',
        borderWidth: 2,
        padding: 10,
        backgroundColor: 'white'
      },
      titulo:{
        textAlign:"center",
        paddingBottom: 5
      },
      botao:{
        flexDirection:'row',
        justifyContent:'center',
        paddingTop: 5
      }
    }
    );
  return (
    <View style={styles.suspeito}>
      <Text style={styles.titulo} >Pessoa</Text>
      <View style={styles.box}> 
      <Checkbox
        status={this.state.Motorista ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Motorista : !this.state.Motorista});
      }}/>
      <Text style={styles.texto}>Motorista</Text>
      </View>

      <View style={styles.box1}>
      <Checkbox
        status={this.state.Empregada ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Empregada : !this.state.Empregada});
      }}/>
      <Text style={styles.texto}>Empregada</Text>
      </View>
      <View style={styles.box}>
      <Checkbox
        status={this.state.Mordomo ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Mordomo : !this.state.Mordomo});
          
      }}/>
      <Text style={styles.texto}>Mordomo</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={this.state.Cozinheiro ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Cozinheiro : !this.state.Motorista});
          setCozinheiro(!this.Cozinheiro);
      }}/>
      <Text style={styles.texto}>Cozinhairo</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={this.state.Esposa ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Motorista : !this.state.Motorista});
          setEsposa(!this.Esposa);
      }}/>
      <Text style={styles.texto}>Esposa</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={this.state.Jardineiro ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Motorista : !this.state.Motorista});
          setJardineiro(!this.Jardineiro);
      }}/>
      <Text style={styles.texto}>Jardineiro</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={this.state.Vizinho ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState({Motorista : !this.state.Motorista});
          setVizinho(!this.Vizinho);
      }}/>
      <Text style={styles.texto}>Vizinho</Text>
      </View>
      <View style={styles.botao}>
      <Button onPress={() => {this.redefinir()}} title='Limpar'/>
      </View>
    </View>
  );
  }
  
}
