import React from 'react';
import { Checkbox } from 'react-native-paper';
import {View, Text, StyleSheet, Button} from 'react-native';




export default function Armas() {
  global.const [Veneno, setVeneno] = React.useState(false);
  global.const [Abajur, setAbajur] = React.useState(false);
  global.const [Revolver, setRevolver] = React.useState(false);
  global.const [Faca, setFaca] = React.useState(false);
  global.const [Tesoura, setTesoura] = React.useState(false);
  global.const [Chave, setChave] = React.useState(false);
  global.const [Martelo, setMartelo] = React.useState(false);

  function redefinir(){
    setVeneno(false);
    setAbajur(false);
    setRevolver(false);
    setFaca(false);
    setTesoura(false);
    setChave(false);
    setMartelo(false); 
  }


  return (
    <View style={styles.armas}>
      <Text style={styles.titulo}>Ferramenta</Text>
      <View style={styles.box}> 
      <Checkbox
        status={Veneno ? 'checked' : 'unchecked'}
        onPress={() => {
          setVeneno(!Veneno);
      }}/>
      <Text style={styles.texto}>Veneno</Text>
      </View>

      <View style={styles.box1}>
      <Checkbox
        status={Abajur ? 'checked' : 'unchecked'}
        onPress={() => {
          setAbajur(!Abajur);
      }}/>
      <Text style={styles.texto}>Abajur</Text>
      </View>
      <View style={styles.box}>
      <Checkbox
        status={Revolver ? 'checked' : 'unchecked'}
        onPress={() => {
          setRevolver(!Revolver);
      }}/>
      <Text style={styles.texto}>Revolver</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Faca ? 'checked' : 'unchecked'}
        onPress={() => {
          setFaca(!Faca);
      }}/>
      <Text style={styles.texto}>Faca</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Tesoura ? 'checked' : 'unchecked'}
        onPress={() => {
          setTesoura(!Tesoura);
      }}/>
      <Text style={styles.texto}>Tesoura de Poda</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Chave ? 'checked' : 'unchecked'}
        onPress={() => {
          setChave(!Chave);
      }}/>
      <Text style={styles.texto}>Chave de Boca</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Martelo ? 'checked' : 'unchecked'}
        onPress={() => {
          setMartelo(!Martelo);
      }}/>
      <Text style={styles.texto}>Martelo</Text>
      </View>

      <Button onPress={redefinir} title='Limpar'/>

    </View>
  );
}

const styles = StyleSheet.create({
  box:{
    backgroundColor:'#ad9d8c', 
    flexDirection:'row',
    justifyContent:'space-between'
  },
  texto:{
    alignSelf: "center",
    paddingLeft: 10,
    paddingRight:10
  },
  box1:{
    backgroundColor:'#e4dacf', 
    flexDirection:'row',
    justifyContent:'space-between'

  },
  armas:{
    borderRadius: 20,
    borderWidth: 2,
    padding: 10,
    borderColor: 'black',
    backgroundColor: 'white'
  },
  titulo:{
    textAlign:"center",
    paddingBottom: 5
  }
}
);
