import {createContext, useState} from 'react';

 const SuspeitoContext = createContext();

 const SuspeitoProviders = ({children}) => {
    const [state, setState] = useState('') 

    const redefinir = () => 
    {
        state = {
            Motorista : false, 
            Empregada : false, 
            Mordomo : false,
            Cozinheiro : false,
            Esposa : false,
            Jardineiro : false,
            Vizinho : false
          };
    }
    return(
        <SuspeitoContext.Provider value={{state, redefinir}}>
            {children}
        </SuspeitoContext.Provider>
    );
}

 export default SuspeitoProviders;
