import React from 'react';
import { Checkbox } from 'react-native-paper';
import {View, Text, StyleSheet, Button} from 'react-native';


export default class Suspeito extends React.Component {
  Motorista;
  Empregada;
  Mordomo; 
  Cozinheiro; 
  Esposa; 
  Jardineiro; 
  Vizinho; 

  setMotorista(){
    if (this.Motorista == true){
      this.Motorista = false;
    }else{
      this.Motorista = true;
    }
  }

  constructor(props) {
    super(props);
    this.Motorista = false; 
    this.Empregada=false;
    this.Mordomo=false;
    this.Cozinheiro=false;
    this.Esposa=false;
    this.Jardineiro=false;
    this.Vizinho =false;
    
  }
  
  redefinir(){
    this.Motorista = false; 
    this.Empregada=false;
    this.Mordomo=false;
    this.Cozinheiro=false;
    this.Esposa=false;
    this.Jardineiro=false;
    this.Vizinho =false;
  }


  render() {
    const styles = StyleSheet.create({
      box:{
        backgroundColor:'#ad9d8c', 
        flexDirection:'row',
        justifyContent:'space-between'
      },
      texto:{
        alignSelf: "center",
        paddingLeft: 10,
        paddingRight:10
      },
      box1:{
        backgroundColor:'#e4dacf', 
        flexDirection:'row',
        justifyContent:'space-between'
    
      },
      suspeito:{
        borderRadius: 20,
        borderColor: 'black',
        borderWidth: 2,
        padding: 10,
        backgroundColor: 'white'
      },
      titulo:{
        textAlign:"center",
        paddingBottom: 5
      },
      botao:{
        flexDirection:'row',
        justifyContent:'center',
        paddingTop: 5
      }
    }
    );
  return (
    <View style={styles.suspeito}>
      <Text style={styles.titulo} >Pessoa</Text>
      <View style={styles.box}> 
      <Checkbox
        status={this.Motorista ? 'checked' : 'unchecked'}
        onPress={() => {
          this.setState(Motorista=true);
      }}/>
      <Text style={styles.texto}>Motorista</Text>
      </View>

      <View style={styles.box1}>
      <Checkbox
        status={this.Empregada ? 'checked' : 'unchecked'}
        onPress={() => {
          setEmpregada(!this.Empregada);
      }}/>
      <Text style={styles.texto}>Empregada</Text>
      </View>
      <View style={styles.box}>
      <Checkbox
        status={this.Mordomo ? 'checked' : 'unchecked'}
        onPress={() => {
          setMordomo(!this.Mordomo);
      }}/>
      <Text style={styles.texto}>Mordomo</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={this.Cozinheiro ? 'checked' : 'unchecked'}
        onPress={() => {
          setCozinheiro(!this.Cozinheiro);
      }}/>
      <Text style={styles.texto}>Cozinhairo</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={this.Esposa ? 'checked' : 'unchecked'}
        onPress={() => {
          setEsposa(!this.Esposa);
      }}/>
      <Text style={styles.texto}>Esposa</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={this.Jardineiro ? 'checked' : 'unchecked'}
        onPress={() => {
          setJardineiro(!this.Jardineiro);
      }}/>
      <Text style={styles.texto}>Jardineiro</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={this.Vizinho ? 'checked' : 'unchecked'}
        onPress={() => {
          setVizinho(!this.Vizinho);
      }}/>
      <Text style={styles.texto}>Vizinho</Text>
      </View>
      <View style={styles.botao}>
      <Button onPress={this.redefinir()} title='Limpar'/>
      </View>
    </View>
  );
  }
  
}
