import React from 'react';
import { Checkbox } from 'react-native-paper';
import {View, Text, StyleSheet} from 'react-native';

export default function Locais() {
  const [Varanda, setVaranda] = React.useState(false);
  const [Escritorio, setEscritorio] = React.useState(false);
  const [Hall_central, setHall_central] = React.useState(false);
  const [Biblioteca, setBiblioteca] = React.useState(false);
  const [Sala_de, setSala_de] = React.useState(false);
  const [Jardineiro, setJardineiro] = React.useState(false);
  const [Vizinho, setVizinho] = React.useState(false);
  
  return (
    <View style={styles.suspeito}>
      <Text>Pessoa</Text>
      <View style={styles.box}> 
      <Checkbox
        status={Varanda ? 'checked' : 'unchecked'}
        onPress={() => {
          setVaranda(!Varanda);
      }}/>
      <Text style={styles.texto}>Varanda</Text>
      </View>

      <View style={styles.box1}>
      <Checkbox
        status={Escritorio ? 'checked' : 'unchecked'}
        onPress={() => {
          setEscritorio(!Escritorio);
      }}/>
      <Text style={styles.texto}>Escritorio</Text>
      </View>
      <View style={styles.box}>
      <Checkbox
        status={Hall_central ? 'checked' : 'unchecked'}
        onPress={() => {
          setHall_central(!Hall_central);
      }}/>
      <Text style={styles.texto}>Hall_central</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Biblioteca ? 'checked' : 'unchecked'}
        onPress={() => {
          setBiblioteca(!Biblioteca);
      }}/>
      <Text style={styles.texto}>Varanda</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Sala_de ? 'checked' : 'unchecked'}
        onPress={() => {
          setSala_de(!Sala_de);
      }}/>
      <Text style={styles.texto}>Sala_de</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Jardineiro ? 'checked' : 'unchecked'}
        onPress={() => {
          setJardineiro(!Jardineiro);
      }}/>
      <Text style={styles.texto}>Jardineiro</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Vizinho ? 'checked' : 'unchecked'}
        onPress={() => {
          setVizinho(!Vizinho);
      }}/>
      <Text style={styles.texto}>Vizinho</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  box:{
    backgroundColor:'#ad9d8c', 
    flexDirection:'row',
    justifyContent:'space-between'
  },
  texto:{
    alignSelf: "center",
    paddingLeft: 10,
    paddingRight:10
  },
  box1:{
    backgroundColor:'#e4dacf', 
    flexDirection:'row',
    justifyContent:'space-between'

  },
  suspeito:{
    borderRadius: 20,
    borderColor: 'black',
    borderWidth: 2,
    padding: 10
  }
}
);
