import React, {useContext} from 'react';
import {ScrollView, StatusBar, StyleSheet, Button, Image, Dimensions} from 'react-native';
import Suspeito from'./Suspeito.js';
import Locais from'./Locais.js';
import Armas from'./Armas.js';

import FormContext from './SuspeitoContext.js';

const state ={
  Motorista : false,
  Empregada : false,
  Mordomo : false,
  Cozinheiro : false,
  Esposa : false,
  Jardineiro : false,
  Vizinho : false
}

const redefinir = () =>{
  state({
    Motorista : false, 
    Empregada : false, 
    Mordomo : false,
    Cozinheiro : false,
    Esposa : false,
    Jardineiro : false,
    Vizinho : false
  });
}

export default function App({navigation}) {
    
    return (
      <ScrollView style={styled.main}>
        <Image
        style={styled.stretch}
        source={require('../assets/titulo.png')}/>
        <StatusBar hidden/>
        <Suspeito redefinir={redefinir()}/>
        <Armas/>
        <Locais/>
        <Button onPress={() => navigation.navigate('ocultado')} title='Ocultar' />
        <Button onPress={() => redefinir()} title='Ocultar' />
      </ScrollView>
    )
  }

  const styled = StyleSheet.create({
    main:{
      backgroundColor: 'black'
    },
    stretch: {
        width: Dimensions.get('window').width,
        height: Dimensions.get('window').height/3.1
  }}
  );
