import React from 'react';
import { Checkbox } from 'react-native-paper';
import {View, Text, StyleSheet, Button} from 'react-native';
import { useState } from 'react';


export default class Suspeito extends React.Component {
  Motorista;
  Empregada;
  Mordomo; 
  Cozinheiro; 
  Esposa; 
  Jardineiro; 
  Vizinho; 


  estilo(){
    const styles = StyleSheet.create({
      box:{
        backgroundColor:'#ad9d8c', 
        flexDirection:'row',
        justifyContent:'space-between'
      },
      texto:{
        alignSelf: "center",
        paddingLeft: 10,
        paddingRight:10
      },
      box1:{
        backgroundColor:'#e4dacf', 
        flexDirection:'row',
        justifyContent:'space-between'
    
      },
      suspeito:{
        borderRadius: 20,
        borderColor: 'black',
        borderWidth: 2,
        padding: 10,
        backgroundColor: 'white'
      },
      titulo:{
        textAlign:"center",
        paddingBottom: 5
      },
      botao:{
        flexDirection:'row',
        justifyContent:'center',
        paddingTop: 5
      }
    }
    );
  }



  constructor(props) {
    super(props);
    this.Motorista = false; 
    this.Empregada=(false);
    this.Mordomo=(false);
    this.Cozinheiro=(false);
    this.Esposa=(false);
    this.Jardineir=(false);
    this.Vizinho =(false);
    this.estilo();
    
  }
  
  redefinir(){
    Motorista.useState(false);
    Empregada.useState(false);
    Mordomo.useState(false);
    Cozinheiro.useState(false);
    Esposa.useState(false);
    Jardineiro.useState(false);
    Vizinho.useState(false);
  }


  render() {
    this.estilo();
  return (
    <View style={styles.suspeito}>
      <Text style={styles.titulo} >Pessoa</Text>
      <View style={styles.box}> 
      <Checkbox
        status={Motorista ? 'checked' : 'unchecked'}
        onPress={() => {
          setMotorista(!Motorista);
      }}/>
      <Text style={styles.texto}>Motorista</Text>
      </View>

      <View style={styles.box1}>
      <Checkbox
        status={Empregada ? 'checked' : 'unchecked'}
        onPress={() => {
          setEmpregada(!Empregada);
      }}/>
      <Text style={styles.texto}>Empregada</Text>
      </View>
      <View style={styles.box}>
      <Checkbox
        status={Mordomo ? 'checked' : 'unchecked'}
        onPress={() => {
          setMordomo(!Mordomo);
      }}/>
      <Text style={styles.texto}>Mordomo</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Cozinheiro ? 'checked' : 'unchecked'}
        onPress={() => {
          setCozinheiro(!Cozinheiro);
      }}/>
      <Text style={styles.texto}>Cozinhairo</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Esposa ? 'checked' : 'unchecked'}
        onPress={() => {
          setEsposa(!Esposa);
      }}/>
      <Text style={styles.texto}>Esposa</Text>
      </View>

      <View style={styles.box1}> 
      <Checkbox
        status={Jardineiro ? 'checked' : 'unchecked'}
        onPress={() => {
          setJardineiro(!Jardineiro);
      }}/>
      <Text style={styles.texto}>Jardineiro</Text>
      </View>

      <View style={styles.box}> 
      <Checkbox
        status={Vizinho ? 'checked' : 'unchecked'}
        onPress={() => {
          setVizinho(!Vizinho);
      }}/>
      <Text style={styles.texto}>Vizinho</Text>
      </View>
      <View style={styles.botao}>
      <Button onPress={redefinir} title='Limpar'/>
      </View>
    </View>
  );
  }
  
}
